$gutter-small-half:  $space-smaller / 2;

$gutter-medium:      $space-small;
$gutter-medium-half: $space-small / 2;

$gutter-large:       $space-large;
$gutter-large-half:  $space-large / 2;

// breakpoints
$screen-xxlarge: 1800;
$screen-xlarge:  1200;
$screen-large:   1024;
$screen-medium:  768;
$screen-small:   540;

// Util bar breakpoints
$util-bar-large: 1330;
$util-bar-medium: 900;

$breakpoint-compare-widget: $screen-medium;
$breakpoint-product-grid-large: 1400;

// content
$content-contained: rem(1280);
$max-width-text: rem(776);

abstractions/z-index.scss:
@function z($layer) {
  @if not map-has-key($z-layers, $layer) {
    @warn "No layer found for `#{$layer}` in $z-layers map. Property omitted.";
  }

  @return map-get($z-layers, $layer);
}

abstractions/text-inputs.scss:
// https://github.com/thoughtbot/bourbon/blob/master/app/assets/stylesheets/addons/_text-inputs.scss

@function assign-inputs($inputs, $pseudo: null) {
  $list: ();

  @each $input in $inputs {
    $input: unquote($input);
    $input: if($pseudo, $input + ":" + $pseudo, $input);
    $list: append($list, $input, comma);
  }

  @return $list;
}

$text-inputs-list: 'input[type="color"]',
                   'input[type="date"]',
                   'input[type="datetime"]',
                   'input[type="datetime-local"]',
                   'input[type="email"]',
                   'input[type="month"]',
                   'input[type="number"]',
                   'input[type="password"]',
                   'input[type="search"]',
                   'input[type="tel"]',
                   'input[type="text"]',
                   'input[type="time"]',
                   'input[type="url"]',
                   'input[type="week"]',
                   'input:not([type])',
                   'textarea';

$all-text-inputs:        assign-inputs($text-inputs-list);
$all-text-inputs-active: assign-inputs($text-inputs-list, active);
$all-text-inputs-focus:  assign-inputs($text-inputs-list, focus);
$all-text-inputs-hover:  assign-inputs($text-inputs-list, hover);

abstractions/svg-encode-function.scss:
// ================================================================ */
//  Function to create an optimized svg url
// from: http://codepen.io/jakob-e/pen/doMoML

@function svg-url($svg){
  //
  //  Chunk up string in order to avoid
  //  "stack level too deep" error
  //
  $encoded:'';
  $slice: 2000;
  $index: 0;
  $loops: ceil(str-length($svg)/$slice);
  @for $i from 1 through $loops {
    $chunk: str-slice($svg, $index, $index + $slice - 1);
    //
    // Encode (may need a few extra replacements)
    //
    $chunk: str-replace($chunk,'"','\'');
    $chunk: str-replace($chunk,'<','%3C');
    $chunk: str-replace($chunk,'>','%3E');
    $chunk: str-replace($chunk,'&','%26');
    $chunk: str-replace($chunk,'#','%23');
    $encoded: #{$encoded}#{$chunk};
    $index: $index + $slice;
  }

  @return url("data:image/svg+xml;charset=utf8,#{$encoded}");
}

//  Helper function to replace characters in a string
@function str-replace($string, $search, $replace: '') {
  $index: str-index($string, $search);
  @if $index {
    @return str-slice($string, 1, $index - 1) + $replace +
      str-replace(str-slice($string, $index +
      str-length($search)), $search, $replace);
  }

  @return $string;
}

abstractions/mixins.scss:
/*----------  Media Queries  ----------*/

@mixin media($max: false, $min: false) {
  @if $max {
    @if $min {
      @media screen and (max-width: #{em($max - 1)}) and (min-width: #{em($min)}) { @content; }
    } @else {
      @media screen and (max-width: #{em($max - 1)}) { @content; }
    }
  } @else {
    @media screen and (min-width: #{em($min)}) { @content; }
  }
}

/*----------  Layout  ----------*/

@mixin container {
  width: 100%;
  padding-right: $gutter-small;
  padding-left: $gutter-small;

  @include media($min: $screen-medium) {
    padding-right: $gutter-medium;
    padding-left: $gutter-medium;
  }

  @include media($min: $screen-large) {
    padding-right: $gutter-large;
    padding-left: $gutter-large;
  }
}

@mixin container-offset {
  margin-right: -1 * $gutter-small;
  margin-left: -1 * $gutter-small;

  @include media($min: $screen-medium) {
    margin-right: -1 * $gutter-medium;
    margin-left: -1 * $gutter-medium;
  }

  @include media($min: $screen-large) {
    margin-right: -1 * $gutter-large;
    margin-left: -1 * $gutter-large;
  }
}

@mixin grid-container {
  display: flex;
  flex-wrap: wrap;
  margin-right: -1 * $gutter-small-half;
  margin-left: -1 * $gutter-small-half;

  @include media($min: $screen-medium) {
    margin-right: -1 * $gutter-medium-half;
    margin-left: -1 * $gutter-medium-half;
  }

  @include media($min: $screen-large) {
    margin-right: -1 * $gutter-large-half;
    margin-left: -1 * $gutter-large-half;
  }
}

@mixin grid-item {
  flex: 0 1 auto;
  margin-right: $gutter-small-half;
  margin-left: $gutter-small-half;

  @include media($min: $screen-medium) {
    margin-right: $gutter-medium-half;
    margin-left: $gutter-medium-half;
  }

  @include media($min: $screen-large) {
    margin-right: $gutter-large-half;
    margin-left: $gutter-large-half;
  }
}

/**
 *
 * Add responsive padding / margins to an element
 * usage:
 *   @include layout-spacing(margin-top);
 * or
 *   @include layout-spacing((margin-top, padding-bottom));
 *
 */

@mixin layout-spacing($space-types) {
  @each $type in $space-types {
    #{$type}: $gutter-small;
  }

  @include media($min: $screen-medium) {
    @each $type in $space-types {
      #{$type}: $gutter-medium;
    }
  }

  @include media($min: $screen-large) {
    @each $type in $space-types {
      #{$type}: $gutter-large;
    }
  }
}

@mixin layout-spacing-half($space-types) {
  @each $type in $space-types {
    #{$type}: $gutter-small-half;
  }

  @include media($min: $screen-medium) {
    @each $type in $space-types {
      #{$type}: $gutter-medium-half;
    }
  }

  @include media($min: $screen-large) {
    @each $type in $space-types {
      #{$type}: $gutter-large-half;
    }
  }
}

@mixin col-width($columns, $full-bleed: false) {
  $width: percentage($columns / 12);

  @if $full-bleed {
    width: $width;
  } @else {
    width: calc(#{$width} - #{$gutter-small});

    @include media($min: $screen-medium) {
      width: calc(#{$width} - #{$gutter-medium});
    }

    @include media($min: $screen-large) {
      width: calc(#{$width} - #{$gutter-large});
    }
  }
}

/*----------  Responsive Product Grids  ----------*/

@mixin product-grid-item-layout {
  @include col-width(6);

  @include media($min: $screen-small) {
    @include col-width(4);
  }

  @include media($min: $screen-large) {
    @include col-width(6);
  }

  @include media($min: $screen-xlarge) {
    @include col-width(4);
  }

  @include media($min: $breakpoint-product-grid-large) {
    @include col-width(3);
  }

  @include media($min: $screen-xxlarge) {
    @include col-width(2);
  }

  // IE only media queries to override nested media querie issue
  @media all and (-ms-high-contrast: none), (-ms-high-contrast: active) {
    width: calc(50% - #{$gutter-medium});

    @media (min-width: 540px) {
      width: calc(25% - #{$gutter-medium});
    }

    @media (min-width: 1024px) {
      width: calc(50% - #{$gutter-large});
    }

    @media (min-width: 1200px) {
      width: calc(33.33333% - #{$gutter-large});
    }

    @media (min-width: 1400px) {
      width: calc(25% - #{$gutter-large});
    }

    @media (min-width: 1800px) {
      width: calc(16.6667% - #{$gutter-large});
    }
  }
}

/*----------  Maintain Aspect Ratio  ----------*/

@mixin maintain-ratio($ratio: 1 1) {
  @if length($ratio) < 2 or length($ratio) > 2 {
    @warn "$ratio must be a list with two values.";
  }

  $width: 100%;
  $height: percentage(nth($ratio, 2) / nth($ratio, 1));

  width: $width;
  padding-bottom: $height;
}


/*----------  Utils  ----------*/

@mixin sr-only {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0,0,0,0);
  border: 0;
}

@mixin reset-button {
  padding: 0;
  font-family: inherit;
  background: transparent;
  border: 0;
  border-radius: 0;
}

/*----------  Text styles  ----------*/

@mixin text-style-smallcaps {
  font-family: $font-primary;
  font-size: $font-size-small-caps;
  letter-spacing: 2px;
  text-transform: uppercase;
}

/*----------  Headings  ----------*/

@mixin heading($size) {
  font-family: $font-primary;
  font-size: map-get($font-size-heading, $size);
  line-height: if($size == 1, $line-height-tight, $line-height);
  color: $color-headings;

  @if $font-primary == "Work Sans" {
    text-transform: uppercase;

    @if $size == 5 or $size == 6 {
      letter-spacing: 1px;
    } @else {
      font-weight: 300;
      letter-spacing: 2px;
    }
  }
}

/*----------  Buttons  ----------*/

@mixin button($style: "primary", $size: "large", $base-style: true) {
  @if $base-style {
    @include text-style-smallcaps;
    @include reset-button;
    position: relative;
    display: inline-block;
    height: map-get($site, button);
    min-width: rem(270);
    padding-right: 1.5em;
    padding-left: 1.5em;
    overflow: hidden;
    font-size: $font-size-body;
    line-height: map-get($site, button);
    text-align: center;
    transition: all 0.2s ease-out;

    @include media($max: $screen-small) {
      font-size: $font-size-body-small;
    }

    &:hover {
      opacity: 1;
    }

    &:active {
      &::before {
        opacity: 0.1;
        transform: scale(0,0);
        transition-duration: 0s;
      }
    }

    &:disabled,
    &.button-disabled {
      cursor: not-allowed;
      opacity: 0.8;
    }

    &::before {
      position: absolute;
      top: 0;
      left: 0;
      display: block;
      width: 100%;
      height: 100%;
      pointer-events: none;
      background: {
        image: radial-gradient(circle, $color-black 10%, transparent 10.01%);
        position: 50%;
        repeat: no-repeat;
      }
      content: "";
      opacity: 0;
      transform: scale(10,10);
      transition: transform 0.2s, opacity 0.4s;
    }
  }

  @if $size == "x-small" {
    height: rem(40);
    font-size: $font-size-body-small;
    line-height: rem(40);
  }

  @if $size == "small" {
    height: rem(48);
    min-width: rem(230);
    line-height: rem(48);
  }

  @if $style == "primary" {
    color: contrast($color-primary, 100%);
    background-color: $color-primary;

    &:hover {
      background-color: scale-color($color-primary, $lightness: -20%);
    }
  }

  @if $style == "secondary" {
    color: $color-primary;
    border: 1px solid $color-primary;
    background-color: transparent;

    &:hover {
      color: contrast($color-primary, 100%);
      background-color: $color-primary;
    }
  }

  @if $style == "white" {
    color: $color-black;
    background-color: $color-white;

    &:hover {
      color: $color-primary;
    }
  }
}

/*---------- Images ----------*/

@mixin lazy-loaded-img() {
  position: absolute;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  margin: auto;
}

base/alerts.scss:
.site-alerts {
  position: fixed;
  top: 0;
  z-index: z("mini-cart");
  width: 100%;
}

.alert {
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  min-height: map-get($site, alert);
  padding: $space-smallest $space-smaller;
  color: $color-white;
  box-shadow: 0 0 0 1px $color-white;
  transition: opacity 0.3s ease;

  &.dismissed {
    opacity: 0;
  }

  &.hidden,
  &.alert-dynamic {
    display: none;
  }

  &.animating,
  &.visible {
    display: block;
  }

  // initial state for enter transition
  &.animating-in,
  &.animating-out {
    opacity: 0;
    transition: opacity 0.3s ease;
  }

  // final states for both in and out
  &.visible {
    opacity: 1;
    transform: translateX(0);
  }

  .alert-dismiss {
    position: absolute;
    top: 50%;
    right: rem(16);
    width: rem(12);
    height: rem(12);
    margin-top: rem(-6);
    line-height: 0;
    color: transparent;
    cursor: pointer;

    &:hover { opacity: 1; }
  }

  &.alert-success,
  &.success {
    background-color: $color-success;

    .alert-dismiss {
      color: darken($color-success, 20%);
    }
  }

  &.alert-error,
  &.error {
    background-color: $color-error;

    .alert-dismiss {
      color: darken($color-error, 30%);
    }
  }

  &.alert-info,
  &.neutral {
    background-color: $color-info;

    .alert-dismiss {
      color: darken($color-info, 30%);
    }
  }

  a:not(.alert-dismiss) {
    color: $color-white;
    text-decoration: underline;

    &:hover {
      text-decoration: none;
    }
  }

  &.out-of-stock-alert {
    @extend %text-style-smallcaps;
    border: 1px solid $color-secondary;
    background: $color-white;
    color: $color-secondary;
    min-width: fit-content;
    width: fit-content;
  } 
}

.stocknotification {
    @extend %text-style-smallcaps;
    padding: $space-smallest $space-smaller;
    text-align: left;
    text-decoration: underline;
}

.oos_notification {
    display: flex;
    align-items: center;
    margin-top: $space-smaller;
}

abstractions/extends.scss:
%sr-only {
  @include sr-only;
}

%reset-button {
  @include reset-button;
}

// typography
%antialiased {
  -webkit-font-smoothing: antialiased;
}

%text-style-smallcaps {
  @include text-style-smallcaps;
}

%h1 { @include heading(1); }
%h2 { @include heading(2); }
%h3 { @include heading(3); }
%h4 { @include heading(4); }
%h5 { @include heading(5); }

%link-style {
  color: $color-primary;
  text-transform: uppercase;
  letter-spacing: 2px;
  transition: opacity 0.2s ease;

  &:hover {
    opacity: 0.7;
  }
}

%text-style-meta {
  font-family: $font-secondary;
  color: $color-text-lighter;
}

// UI
// Checkbox
%checkmark {
  transition: stroke-dashoffset 0.4s ease-in-out;
  stroke-dasharray: 50 50;
  stroke-dashoffset: 50;
}

%checkmark-visible {
  stroke-dashoffset: 0;
}

%checkbox-unchecked {
  display: inline-block;
  width: rem(18);
  height: rem(18);
  border: 1px solid $color-borders;
  transition: all 0.2s ease-out 0.4s;
}

%checkbox-checked {
  background-color: $color-primary;
  border-color: transparent;
  transition: all 0.2s ease-out;
}

%nav-separator {
  display: block;
  width: calc(100% - #{$space-large});
  height: rem(1);
  margin: 0 auto;
  background-color: rgba($color-white, 0.3);
  content: "";

  @include media($min: $screen-small) {
    @include layout-spacing(margin-left);
    width: rem(75);
    background-color: $color-borders;
  }
}

// Tabs
%tab-style {
  @extend %text-style-smallcaps;
  display: block;
  max-height: rem(60);
  line-height: rem(60);
  color: $color-text-lighter;
  background: $color-highlight;

  &:hover {
    color: $color-primary;
  }

  &.active {
    color: $color-white;
    background: $color-primary;
  }
}

base/base.scss:
html {
  @extend %antialiased;
  height: 100%;
  box-sizing: border-box;
}

*,
*::before,
*::after {
  box-sizing: inherit;
}

body {
  position: relative;
  overflow-x: hidden;
  font-family: $font-primary;
  font-size: $font-size-body;
  line-height: $line-height;
  color: $color-text;
  background-color: $color-background;
}

a {
  color: inherit;
  text-decoration: none;
  transition: opacity 0.2s ease;

  &:hover {
    opacity: 0.7;
  }

  .user-content & {
    color: $color-primary;
  }
}

img {
  height: auto;
  max-width: 100%;
  vertical-align: middle;
}

ul {
  padding-left: 0;
  margin: 0;
  list-style-type: none;
}

.sr-only {
  @extend %sr-only;
}

.is-hidden {
  display: none;
}

.scroll-locked {
  height: 100%;
  overflow: hidden;

  &.page-category {
