base/typography.scss:
// Reset all headings to a standard size / margin
h1,
h2,
h3,
h4,
h5,
h6 {
  margin-top: 0;
  margin-bottom: 0;
  font-size: $font-size-body;
  font-weight: normal;
  line-height: $line-height;

  a { color: inherit; }
}

// Styles for user-content areas
.user-content {
  h1,
  h2,
  h3,
  h4,
  h5,
  h6 {
    margin-bottom: 0.5em;

    &:first-child { margin-top: 0; }
    &:last-child { margin-bottom: 0; }
  }

  h1 { @extend %h1; }
  h2 { @extend %h2; }
  h3 { @extend %h3; }
  h4 { @extend %h4; }
  h5 { @extend %h5; }
  h6 { @extend %h6; }

  ul {
    padding-left: $space-large;
    margin: $space-smaller 0;
    list-style-type: disc;
  }
