base/images.scss:
.ratio-16-9 {
  @include maintain-ratio(16 9);
}

.ratio-4-3 {
  @include maintain-ratio(4 3);
}

.ratio-1-1 {
  @include maintain-ratio(1 1);
}

.ratio-3-4 {
  @include maintain-ratio(3 4);
}

.ratio-10-2 {
  @include maintain-ratio(10 2);
}

.ratio-10-4 {
  @include maintain-ratio(10 4);
}

base/buttons.scss:
.button {
  @include button(false, false);
  border-radius: 35px;

  &-x-small {
    @include button(false, x-small, false);
  }

  &-small {
    @include button(false, small, false);
  }

  &-primary {
    @include button(primary, false, false);
      color: $color-white; 
  }

  &-secondary {
    @include button(secondary, false, false);
  }

  &-white {
    @include button(white, false, false);
  }

  & + .button {
    @include media($min: $screen-medium) {
      margin-left: 1.25em;
    }
  }
}

// progress button
.button-progress {
  position: relative;

  &.in-progress {
    .button-text {
      visibility: hidden;
    }
  }

  .complete-text {
    position: absolute;
    left: 0;
    display: inline-block;
    width: 100%;
    height: 100%;
    vertical-align: middle;
    visibility: hidden;

    span {
      margin: auto;
    }

    .checkmark {
      @extend %checkmark;
    }
  }

  &.will-complete {
    .complete-text {
      visibility: visible;
    }

    .checkmark {
      @extend %checkmark-visible;
      transition-delay: 0.5s;
    }
  }
}

.overlay-button {
  position: absolute;
  top: 0;
  left: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  height: 100%;
}

.spinner {
  width: $space-smaller;
  height: $space-smaller;
  color: inherit;
  pointer-events: none;
  border: 2px solid;
  border-bottom-color: transparent;
  border-radius: 50%;
  animation: 0.8s loader linear infinite;
}

@keyframes loader {
  0% { transform: rotate(0deg); }

  100% { transform: rotate(360deg); }
}

base/forms.scss:
// layout
.form-field-control {
  @extend %clearfix;
  display: block;
}

.form-inline-message {
  display: inline-block;
  margin-top: $gutter-small;
  color: $color-error;

  .search-form & {
    position: absolute;
    margin-top: $space-smallest;
  }
}

.form-inline {
  display: flex;

  .form-input {
    margin-right: $space-small;
  }

  .button {
    flex: 0 0 auto;
  }
}

.form-input {
  width: 100%;
  height: rem(48);
  padding: $gutter-medium-half $space-smaller;
  font-family: $font-primary;
  background: transparent;
  border: 1px solid $color-borders;
  border-radius: 0;
  box-shadow: none;
  -webkit-appearance: none;

  &::placeholder {
    color: $color-text-lighter;
  }

  &:focus {
    background: none;
    outline: none;
  }

  &.form-checkbox,
  &.form-radio {
    @extend %sr-only;
  }

  &.form-file {
    display: flex;
    flex: 0 0 1;
  }

  .form-inline & {
    display: inline-block;
    vertical-align: middle;
  }
}

textarea {
  height: auto !important; //override above input height for multiline
}

.form-fieldset {
  padding: 0;
  margin: 0;
  border: 0;
}

.form-label {
  display: block;
  padding-bottom: $gutter-medium-half;
  color: $color-text;

  .product-options & {
    min-width: rem(125); // to give enough room when all values are short for dropdown arrow display
  }
}

.form-field-title {
  @extend %text-style-smallcaps;
  display: inline-block;
  margin-bottom: $space-smallest;
  color: $color-text;
  font-family: $font-primary;

  .form-required &::after {
    color: $color-error;
    content: "*";
  }
}

// Required form element display 'required' text next title
.required-text {
  @extend .sr-only;
}

.form-label-text {
  position: relative;
}

// checkboxes
input[type=checkbox] + .form-label-text {
  &::before {
    @extend %checkbox-unchecked;
    position: absolute;
    top: 50%;
    left: 0;
    content: "";
    transform: translateY(-50%);
  }
}

input[type=checkbox]:checked + .form-label-text::before {
  @extend %checkbox-checked;
  color: $color-white;
  text-align: center;
  background-image: svg-url('<svg xmlns="http://www.w3.org/2000/svg" width="9" height="7" viewBox="0 0 16 16"><path class="checkmark" fill="none" stroke="#{$color-white}" stroke-width="2" stroke-miterlimit="10" d="M1 8l5 6 9-11"/></svg>');
  background-position: center center;
  background-repeat: no-repeat;
  content: "";
}

// radio buttons
.shipping-calculator .form-field-radio {
  span {
    @extend .form-label-text;
    position: relative;
  }
}

input[type=radio] + .form-label-text,
input[type=radio] + span {
  &::before {
    position: absolute;
    top: 50%;
    left: 0;
    display: inline-block;
    width: rem(18);
    height: rem(18);
    border: 1px solid $color-borders;
    border-radius: 50%;
    content: "";
    transform: translateY(-50%);
  }
}

input[type=radio]:checked + .form-label-text::before,
input[type=radio]:checked + span::before {
  background-color: $color-primary;
  background-image: svg-url('<svg xmlns="http://www.w3.org/2000/svg" height="16" width="16"><circle cx="8" cy="8" r="4" fill="#{$color-white}" /></svg> ');
  background-position: 50% center;
  background-repeat: no-repeat;
  border: 1px solid $color-primary;
  content: "";
}

// ---------------------------------------------------------------- //
// Select Wrappers - Provides some additional style control for select elements

.form-select-wrapper {
  position: relative;
  display: block;
  cursor: pointer;

  &::after {
    position: absolute;
    top: calc(50% - #{rem(4)});
    right: $gutter-small-half;
    width: $gutter-large-half / 2;
    height: $gutter-large-half / 2;
    pointer-events: none;
    background-image: svg-url('<svg width="18" height="18" viewBox="0 0 18 18" xmlns="http://www.w3.org/2000/svg"><path id="a" d="M12 8l-3 3-3-3h6z" fill="#{$color-text-lighter}" /></svg>');
    background-position: 50% center;
    background-repeat: no-repeat;
    content: "";
  }

  .form-select {
    width: 100%;
    background: transparent;
    -moz-appearance: none;

    &:hover {
      border-color: $color-text-lighter;
    }

    option {
      color: $color-black;
    }
  }
}

.form-selected-text {
  display: inline-block;
  padding: rem(10) 0;
  vertical-align: middle;
}

// Hide HTML5 Up and Down arrows in number input[type="number"]
input[type="number"]::-webkit-outer-spin-button,
input[type="number"]::-webkit-inner-spin-button {
  appearance: none;
  margin: 0;
}

input[type="number"] {
  -moz-appearance: textfield;
}

base/loading.scss:
.loading-overlay {
  position: absolute;
  z-index: z(loading-overlay);
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  display: flex;
  background-color: rgba(contrast($color-primary, 100%), 0.8);

  body > & {
    position: fixed;
  }
}

.loading,
.loading:after {
  border-radius: 50%;
  width: 4em;
  height: 4em;
}

.loading {
  position: relative;
  margin: auto;
  font-size: 10px;
  text-indent: -9999em;
  border-top: 0.6em solid rgba($color-primary, 0.2);
  border-right: 0.6em solid rgba($color-primary, 0.2);
  border-bottom: 0.6em solid rgba($color-primary, 0.2);
  border-left: 0.6em solid $color-primary;
  transform: translateZ(0);
  animation: loading 0.8s infinite linear;
}

@keyframes loading {
  0% {
    transform: rotate(0deg);
  }

  100% {
    transform: rotate(360deg);
  }
}

layout/layout.scss:
.sidebar-is-visible {
  @include media($max: $screen-large) {
    position: fixed;
    overflow: hidden;
  }
}

.site-main {
  margin-top: map-get($site, header-mobile);
  overflow: hidden;

  @include media($min: $screen-small) {
    display: flex;
    margin-top: map-get($site, header);
  }
}

.site-main-sidebar {
  width: 100%;
  overflow: hidden;

  // Offcanvas nav
  @include media($max: $screen-large) {
    position: fixed;
    top: map-get($site, header-mobile);
    right: 100%;
    bottom: 0;
    z-index: z("sidebar");
    transition: transform 0.2s ease-out;

    .sidebar-is-visible & {
      transform: translate3d(100%, 0, 0);
    }
  }

  @include media($min: $screen-small, $max: $screen-large) {
    top: map-get($site, header);
  }

  @include media($min: $screen-small) {
    flex: 0 0 auto;
    width: map-get($site, sidebar);
    color: $color-text;
    margin-top: $space;
  }

  .site-utils-small {
    color: $color-white;

    &::before {
      @extend %nav-separator;
    }

    @include media($min: $screen-medium) {
      display: none;
    }

    .main-navigation-container {
      padding-top: map-get($site, breadcrumbs);
    }
  }
}

.site-main-sidebar-inner {
  width: 100%;
  height: 100%;
  overflow-x: hidden;
  overflow-y: auto;
  background-color: $color-primary;

  @include media($max: $screen-small) {
    position: absolute;
    top: 0 !important;
    bottom: 0;
  }

  @include media($min: $screen-large) {
    width: map-get($site, sidebar);
    height: 100%;
    max-height: calc(100vh - #{map-get($site, header)});
    background-color: $color-background;
    border-right: 0px solid $color-borders;
  }

  @include media($min: $screen-large) {
    &.is-sticky {
      position: fixed;
      top: map-get($site, header);
      left: 0;
      padding-top:$space-large;
    }
  }
}

.site-main-content {
  @include container;
  @include layout-spacing(padding-bottom);

  @include media($min: $screen-large) {
    width: calc(100% - #{map-get($site, sidebar)});
  }

  .page-account_addressbook & {
    overflow-x: hidden;
  }
}

.page-content {
  max-width: $max-width-text;
  margin-right: auto;
  margin-left: auto;
  font-family: $font-secondary;
}

.page-content-program {
  margin-right: auto;
  margin-left: auto;
  font-family: $font-secondary;
}

layout/site-header.scss:
$local-sidebar-width: map-get($site, sidebar);
$local-offset-large: $gutter-large * 2;
$local-offset-medium: $gutter-medium * 2;

// layout
.site-header {
  position: fixed;
  top: 0;
  z-index: z("header");
  display: flex;
  align-items: center;
  height: map-get($site, header-mobile);
  background-color: $color-white;
  border-bottom: 0px solid $color-borders;

  @include media($min: $screen-small) {
    height: map-get($site, header);
  }

  &-section {
    @include grid-item;
    display: flex;
    align-items: center;
  }

  > .grid-container {
    align-items: flex-start;
    flex: 1;
    flex-wrap: wrap;
    height: map-get($site, header-mobile);
    padding-bottom: rem(10);

    @include media($max: $screen-small) {
      width: 100%;
    }

    @include media($min: $screen-small) {
      align-items: center;
      flex-wrap: nowrap;
      padding: 0;
    }
  }
}

// header sections layouts NOTE: logo-position-left is the default layout
.header-section-branding {
  @include col-width(8);
  order: 1;

  @include media($max: $screen-medium) {
    height: map-get($site, top-bar-mobile);
  }

  @include media($min: $screen-small) {
    @include col-width(7);
  }

  @include media($min: $screen-large) {
    width: auto;
    padding-bottom: 0;
    /*margin-right: 6.5rem;*/

    .logo-position-center & {
      flex: 1 0 auto;
      order: 2;
    }

    .logo-position-right & {
      order: 3;
    }
  }
}

.header-section-search {
  @include col-width(12);
  position: relative;
  order: 3;

  @include media($max: $screen-small) {
    height: map-get($site, search-mobile);
  }

  @include media($min: $screen-small) {
    order: 2;
  }

  @include media($min: $screen-large) {
    flex: 1 0 auto;
    width: auto;

    .logo-position-center & {
      order: 1;
      width: 100%;
      max-width: rem(215);
    }
  }
}

.header-section-utils {
  @include col-width(4);
  justify-content: flex-end;
  order: 2;

  @include media($max: $screen-medium) {
    height: map-get($site, top-bar-mobile);
  }

  @include media($min: $screen-small) {
    order: 3;
  }

  @include media($min: $screen-large) {
    justify-content: flex-start;
    width: auto;
    padding-bottom: 0;

    .logo-position-right & {
      order: 1;
    }
  }
}

// branding
.site-header-branding {
  max-width: calc(100% - #{rem(36)});

  &.has-logo {
    height: auto;
  }

  @include media($min: $screen-medium) {
    flex: 0 0 auto;
  }

  @include media($min: $screen-large) {
    max-width: 100%;

    .logo-position-left &,
    .logo-position-right & {
      width: calc(#{$local-sidebar-width} - #{$local-offset-large});
    }

    .logo-position-center & {
      width: 100%;
      text-align: center;
    }
  }

  .logo-image {
    display: block;
    width: 100%;
    width: auto 0;
    /*padding-top: rem(2);
    padding-bottom: rem(2);*/

    img {
      display: block;
      max-height: map-get($site, top-bar-mobile);

      @media
      not screen and (-webkit-min-device-pixel-ratio: 2),
      not screen and (   min--moz-device-pixel-ratio: 2),
      not screen and (     -o-min-device-pixel-ratio: 2/1),
      not screen and (        min-device-pixel-ratio: 2),
      not screen and (                min-resolution: 192dpi),
      not screen and (                min-resolution: 2dppx) {
        image-rendering: -webkit-optimize-contrast;
      }

      @include media($min: $screen-small) {
        max-height: $space-large;
        margin: auto;
      }
    }
  }

  .logo-text {
    a {
      display: block;
      overflow: hidden;
      text-overflow: ellipsis;
      white-space: nowrap;
    }
  }
}

// burger
.button-navigation-toggle {
  @extend %reset-button;
  position: relative;
  display: block;
  flex: 0 0 auto;
  width: rem(18);
  height: rem(12);
  margin-right: rem(18);
  cursor: pointer;

  @include media($min: $screen-large) {
    display: none;
  }

  &:focus {
    outline: none;

    &::before,
    &::after,
    .burger {
      background-color: $color-primary;
    }
  }

  &::before,
  &::after {
    @extend .burger;
    content: "";
    transition: top 0.1s linear 0.1s, transform 0.1s linear;
  }

  &::before {
    top: 0;
  }

  &::after {
    top: 100%;
  }

  .burger {
    top: 50%;
  }

  .sidebar-is-visible & {
    &::before,
    &::after {
      top: 50%;
      transition: top 0.1s linear, transform 0.1s linear 0.1s;
    }

    &::before {
      transform: rotate(-45deg);
    }

    &::after {
      transform: rotate(45deg);
    }

    .burger {
      opacity: 0;
    }
  }
}

.burger {
  position: absolute;
  display: block;
  width: 100%;
  height: 2px;
  margin-top: -1px;
  background-color: $color-text;
}

// utils
.site-header-utils {
  @extend %text-style-smallcaps;
  display: flex;

  > * {
    @include grid-item;
  }

  > :first-child {
    margin-left: 0;
  }

  > :last-child {
    margin-right: 0;

    @include media($min: $screen-medium) {
      margin-right: $gutter-medium-half;
    }
  }

  .currency-selector {
    @include media($max: $screen-medium) {
      display: none;
    }
  }
}

.account-link {
  display: flex;
  align-items: center;

  &-text {
    margin-left: 0.8125rem;

    @include media($max: $screen-large) {
      display: none;
    }
  }

  &-dropdown {
    align-self: flex-start;
    margin-left: rem(6);

    svg {
      position: relative;
      top: -2px;
    }
  }

  &:hover {
    color: $color-primary;
  }
}

// account dropdown panel
.account-dropdown-panel {
  right: 0;
  width: rem(175);
  padding: $gutter-small;
  margin-top: $gutter-small;
  font-family: $font-primary;
  font-weight: 400;
  background: $color-highlight;
  border: 1px solid $color-borders;

  @include media($min: $screen-large) {
    right: 50%;
    margin-right: rem(-87);
  }
}

.account-dropdown-item {
  padding-top: $space-smallest / 2;
  padding-bottom: $space-smallest / 2;

  &:first-child {
    padding-top: 0;
  }

  &:last-child {
    padding-top: $gutter-medium-half;
    margin-top: $gutter-medium-half;
    border-top: 1px solid $color-borders;
  }
}

.account-utils {
  &:hover {
    color: $color-primary;
  }
}

.mini-cart-toggle {
  display: flex;
  align-items: center;

  svg {
    display: inline-block;
    vertical-align: middle;
  }

  &:hover {
    color: $color-primary;
  }
}

.mini-cart-icon-wrap {
  &.cart-empty {
    .mini-cart-icon-full {
      display: none;
    }
  }

  &.cart-full {
    .mini-cart-icon-empty {
      display: none;
    }

    .mini-cart-icon-full {
      display: inline-block;
    }
  }
}

.mini-cart-count {
  display: none;
  margin-left: 0.8125rem;

  &.show {
    display: inline-block;
  }
}

layout/site-footer.scss:
// layout
.site-footer-upper {
  &.light {
    background-color: $color-highlight;
  }

  &.dark {
    background-color: #606060;
  }
}

.site-footer-upper-container {
  display: flex;
  align-items: flex-start;
  flex-wrap: wrap;
  justify-content: space-around;
  max-width: $content-contained;
  padding: $gutter-large $gutter-small;
  margin: 0 auto;

  @include media($min: $screen-medium) {
    padding-top: $space-largest;
    padding-bottom: $space-largest;
  }

  @include media($min: $screen-large) {
    padding-right: $gutter-large;
    padding-left: $gutter-large;
  }
}

// footer sections
.site-footer-section {
  @include col-width(12);
  flex-grow: 1;
  margin-bottom: $gutter-large;
  text-align: center;

  &:last-child {
    margin-bottom: 0;
  }

  @include media($min: $screen-medium) {
    @include col-width(3);
    @include grid-item;
    margin-bottom: 0;
    text-align: center;
  }

  @include media($max: $screen-large, $min: $screen-medium) {
    text-align: left;
  }

  &.site-footer-newsletter,
  &.site-footer-address {
    flex-grow: 2;
  }

  &.site-footer-disable {
    display: none;
  }
}

.site-footer-section:first-child:nth-last-child(4),
.site-footer-section:first-child:nth-last-child(4) ~ .site-footer-newsletter {
  .newsletter-subscription-form {
    @include media($min: $screen-medium) {
      .form-inline {
        display: flex;

        .newsletter-input {
          width: 100%;
          max-width: 100%;
          margin-bottom: $space-smaller;
        }
      }
    }
  }
}

// footer address
.footer-address-heading {
  @extend %text-style-smallcaps;
  display: inline-block;
  margin-bottom: $space-smallest;
  font-weight: 500;

  .dark & {
    color: $color-white;
  }
}

.footer-address-content {
  font-family: $font-primary;
  font-weight: 300;
  color: iromi_type($color-highlight, "less");

  .dark & {
    color: rgba($color-white, 0.8);
  }

  p {
    margin: 0;
  }
}

.site-footer-lower {
  @include container;
  display: flex;
  align-items: center;
  flex: 1 0 auto;
  flex-wrap: wrap;

  @include media($min: $screen-xlarge) {
    justify-content: space-between;
  }
}

// footer credits
.site-footer-credits {
  @include col-width(12);
  font-family: $font-primary;
  font-weight: 300;
  text-align: center;

  @include media($min: $screen-xlarge) {
    @include col-width(7);
    text-align: left;
  }
}

.site-footer-credits-list {
  margin: $space-smaller 0;
}

.site-footer-payment-list {
  &.footer-has-trust-seal {
    margin-top: 10px;
  }
}

.site-footer-payment-list-item,
.site-footer-credits-list-item {
  display: inline-block;
  color: $color-text-lighter;

  p {
    margin: 0;
  }
}

// footer payments
.site-footer-payment {
  @include col-width(12);
  margin-top: $gutter-small;
  text-align: center;

  @include media($min: $screen-xlarge) {
    @include col-width(5);
    margin-top: 0;
    text-align: right;
  }
}

.site-footer-payment-list-item {
  padding-right: $gutter-small;
  vertical-align: middle;

  &:last-child {
    padding-right: 0;
  }
}

// Trust seals
table.geotrust-ssl-seal-table {
  display: inline-block;
  margin-top: 10px;
  margin-bottom: 20px;
  margin-left: $gutter-small;
  vertical-align: middle;
  border: 0;

  td {
    padding: 0;
    border: 0;
  }
}

// EU Cookie notification
.cookie-message {
  position: sticky;
  bottom: 0;
  width: 100%;
  padding-bottom: 20px;
  margin-bottom: 0 !important;
  background: rgba($color-background,0.95);
  border-top: 1px solid $color-borders;
}

components/breadcrumbs.scss:
.breadcrumbs {
  @extend %text-style-smallcaps;
  @include container-offset;
  @include layout-spacing((padding-right, padding-left));
  height: map-get($site, breadcrumbs);
  padding-top: 1.25em;
  padding-bottom: 1.25em;
  overflow-x: auto;
  overflow-y: hidden;
  color: $color-text;
  text-align: center;
  white-space: nowrap;
  /*background-color: $color-highlight;*/
  border-bottom: 0px solid $color-borders;
  font-size: 0.5rem;
  margin-top: $space;
}

components/apple-pay.scss:
.apple-pay-checkout-button {
  display: none;
  box-sizing: border-box;
  min-width: 50px;
  height: 60px;
  background: {
    size: 100% 60%;
    repeat: no-repeat;
    position: 50% 50%;
  }
  border-radius: 5px;

  @if stencilString("style_apple_pay_button") == "white" {
    background-image: -webkit-named-image(apple-pay-logo-black);
    background-color: white;
  }

  @else if stencilString("style_apple_pay_button") == "white-border" {
    background-image: -webkit-named-image(apple-pay-logo-black);
    background-color: white;
    border: 1px solid black;
  }

  @else {
    background-image: -webkit-named-image(apple-pay-logo-white);
    background-color: black;
  }

  .additional-checkout-buttons & {
    margin: 15px 0;
  }

  .apple-pay-supported & {
    display: block;
  }
}

components/banners.scss:
.banners {
  @include container-offset;
  @include layout-spacing((padding-right, padding-left));
  padding-top: 1.5em;
  padding-bottom: 1.5em;
  color: $color-text-light;
  background-color: $color-highlight;
  border-bottom: 1px solid $color-borders;
}

.banners-top {
  .carousel + & {
    border-top: 1px solid $color-borders;
  }
}

.banners-bottom {
  @include layout-spacing(margin-top);
  margin-bottom: -1 * $gutter-small;
  border-top: 1px solid $color-borders;

  @include media($min: $screen-medium) {
    margin-bottom: -1 * $gutter-medium;
  }

  @include media($min: $screen-large) {
    margin-bottom: -1 * $gutter-large;
  }
}

.banner {
  p {
    &:first-child {
      margin-top: 0;
    }

    &:last-child {
      margin-bottom: 0;
    }
  }
}

// cart level discount banners
.promo-message {
  @extend .banners;
  text-align: center;
  text-transform: uppercase;
  letter-spacing: 1px;
  border-bottom: none;
}

components/carousel.scss:
.carousel {
  @include container-offset;
  overflow: hidden;

  .flickity-viewport {
    transition: height 0.2s ease-out;
  }
}

// Slides
.carousel-item {
  position: relative;
  width: 100%;
  margin: 0;
}

.carousel-item-image {
  background: {
    size: cover;
    position: 50%;
  }
  opacity: 0;
  transition: opacity 0.3s ease-out;

  &.flickity-bg-lazyloaded {
    opacity: 1;
  }

  img {
    opacity: 0;
  }

  &:not(.ratio-any) {
    img {
      @include sr-only;
    }
  }

  &.ratio-vh {
    height: 90vw;

    @include media($min: $screen-small) {
      height: calc(100vh - #{map-get($site, header)});
    }
  }
}

// Slide nav
.carousel-nav {
  position: absolute;
  bottom: 100%;
  padding: 0;
  margin: 0;
  border: 0;

  @include media($max: $screen-small) {
    @include layout-spacing(left);
  }
}

.carousel-nav-item {
  @extend %reset-button;
  display: block;
  float: left;
  width: rem(50);
  height: rem(50);
  background-color: rgba($color-white, 0.9);

  &:first-child {
    border-right: 1px solid contrast($color-white, 15%);
  }

  svg {
    pointer-events: none;
  }

  .carousel-style-dark & {
    color: contrast($color-black, 95%);
    background-color: rgba($color-black, 0.9);

    &:first-child {
      border-right: 1px solid contrast($color-black, 20%);
    }
  }

  .carousel-style-accent & {
    color: $color-white;
    background-color: rgba($color-primary, 0.9);

    &:first-child {
      border-right: 1px solid rgba(contrast($color-primary, 10%), 0.8);
    }
  }
}

// Slide text
.carousel-item-info-wrapper {
  position: relative;
  opacity: 0;
  transition: all 0.4s ease-out 0.4s;

  @include media($min: $screen-small) {
    @include container;
    position: absolute;
    bottom: 0;
    transform: translateY(#{$space-large});
  }

  .is-selected & {
    opacity: 1;
    transform: translateY(0);
  }
}

.carousel-item-info {
  overflow: hidden;
  max-width: rem(552);
  padding-top: $gutter-small;
  padding-right: $gutter-small;
  padding-left: $gutter-small;
  background-color: $color-white;

  @include media($min: $screen-small) {
    padding-top: $gutter-large;
    padding-right: $gutter-large;
    padding-left: $gutter-large;
  }

  .carousel-style-light &,
  .carousel-style-dark & {
    .button {
      @include button;
    }
  }

  .carousel-style-dark & {
    color: $color-white;
    background-color: $color-black;
  }

  .carousel-style-accent & {
    color: $color-white;
    background-color: $color-primary;

    .button {
      @include button($style: "white");
      border-bottom: 1px solid $color-primary;
    }
