}

.carousel-item-title {
  @include heading(2);
  margin-bottom: rem(18);
  color: inherit;

  @include media($min: $screen-small) {
    @include heading(1);
    color: inherit;
  }
}

.carousel-item-text {
  margin-bottom: $space-large;
  font-family: $font-secondary;
  line-height: $line-height-loose;

  @if $font-primary == 'Work Sans' {
    font-weight: 300;
  }

  .carousel-style-dark & {
    opacity: 0.8;
  }
}

.carousel-button-wrapper {
  line-height: 0;
}

components/link-lists.scss:
