product-catalog/compare-widget.scss:
.compare-widget {
  position: fixed;
  right: 0;
  bottom: 0;
  z-index: z(compare-widget);
  display: none;
  width: 100%;
  padding: $space-smallest $space-small 0;
  background: $color-highlight;
  border-top: 1px solid $color-borders;
  transition: map-get($transition, compare-widget);

  @include media($min: $breakpoint-compare-widget) {
    @include layout-spacing((right, margin-left));
    width: auto;
    padding-top: $space-small;
    border-right: 1px solid $color-borders;
    border-left: 1px solid $color-borders;
  }

  &.animating,
  &.visible {
    display: block;
  }

  &.animating-in,
  &.animating-out {
    transform: translateY(100%);
  }

  &.visible {
    transform: translateY(0);
    opacity: 1;

    @include media($max: $breakpoint-compare-widget) {
      .util-active & {
        transform: translateY(-1 * map-get($site, button));
      }
    }
  }

  &[data-compare-count="1"] {
    .compare-multiple {
      display: none;
    }
  }

  &:not([data-compare-count="1"]) {
    .compare-one {
      display: none;
    }
  }
}

.compare-widget-header {
  text-align: center;
  margin-bottom: $space-small;

  @include media($min: $breakpoint-compare-widget) {
    display: flex;
    align-items: center;
    justify-content: space-between;
    text-align: left;
  }
}

.compare-widget-toggle {
  @extend %reset-button;
  @extend %text-style-smallcaps;

  @include media($min: $breakpoint-compare-widget) {
    display: none;
  }

  &.is-open {
    svg {
      transform: rotate(180deg);
    }
  }

  .compare-one,
  .compare-multiple {
    display: block;
    margin-top: $space-smallest;
  }
}

.compare-widget-content {
  @include media($min: $breakpoint-compare-widget) {
    display: flex;
    align-items: center;
    justify-content: space-between;
  }
}

.compare-widget-cancel {
  @extend %text-style-smallcaps;
  @extend %reset-button;
  color: $color-text-light;

  @include media($min: $breakpoint-compare-widget) {
    display: none;
  }
}

.compare-widget-action {
  @include media($max: $breakpoint-compare-widget) {
    @include text-style-smallcaps;
    margin-left: $space;
    color: $color-primary;
  }

  @include media($min: $breakpoint-compare-widget) {
    @include button(secondary, x-small);
    min-width: 0;
  }
}

.compare-widget-cta {
  @extend %text-style-smallcaps;
  margin-right: $space-small;
  color: $color-text-lighter;

  @include media($max: $breakpoint-compare-widget) {
    display: none;
  }
}

.compare-widget-remove-all {
  @extend %reset-button;
  color: $color-primary;
  font-size: $font-size-body-small;

  @include media($max: $breakpoint-compare-widget) {
    display: none;
  }
}

.compare-widget-items-wrap {
  @include media($max: $breakpoint-compare-widget) {
    display: none;
    padding-top: $space-small;
    padding-bottom: $space-small;
  }

  @include media($min: $breakpoint-compare-widget) {
    display: block !important;
  }
}

.compare-widget-items {
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  vertical-align: middle;

  @include media($min: $breakpoint-compare-widget) {
    margin-left: -1 * ($space-small / 2);
  }
}

.compare-widget-item {
  position: relative;
  display: none;
  margin-right: $space-small / 2;
  margin-bottom: $space-small;
  margin-left: $space-small / 2;
  transition: all 0.2s ease;

  &.animating,
  &.visible {
    display: block;
  }

  &.animating-in,
  &.animating-out {
    transform: scale(0);
    opacity: 0;
  }

  // If 3 items, make it 3 up
  &:first-child:nth-last-child(3),
  &:first-child:nth-last-child(3) ~ & {
    .compare-widget-item-image {
      @include media($max: $screen-small) {
        width: calc(33.3333vw - #{$space-large});
        height: calc(33.3333vw - #{$space-large});
      }
    }
  }
}

.compare-widget-item-image {
  width: calc(50vw - #{$space-larger});
  height: calc(50vw - #{$space-larger});
  background: {
    size: contain;
    position: 50% 50%;
    repeat: no-repeat;
    color: $color-white;
  }

  @include media($min: $screen-small) {
    width: rem(92);
    height: rem(92);
  }
}

.compare-widget-item-remove {
  @extend %reset-button;
  @extend %text-style-smallcaps;
  width: 100%;
  color: $color-text-lighter;
  text-align: center;

  @include media($min: $breakpoint-compare-widget) {
    position: absolute;
    top: 0;
    height: 100%;
    color: transparent;

    &:before {
      display: flex;
      align-items: center;
      justify-content: center;
      height: 100%;
      font-size: rem(20);
      color: $color-black;
      background-color: rgba($color-white, 0.7);
      opacity: 0;
      transition: opacity 0.2s ease-out;
      content: '\00d7';
    }

    &:hover {
      &:before {
        opacity: 1;
      }
    }
  }
}

.compare-widget-footer {
  flex: 0 0 auto;
  padding-bottom: $space-small;
  vertical-align: middle;

  @include media($max: $breakpoint-compare-widget) {
    text-align: center;
  }

  @include media($min: $breakpoint-compare-widget) {
    margin-left: $space-large - ($space-small / 2);
  }
}

product-catalog/faceted-search.scss:
.facet-block-container {
  @include media($max: $util-bar-medium) {
    display: none;

    &.is-visible {
      display: block;
    }
  }

  @include media($max: $util-bar-large, $min: $screen-large) {
    display: none;

    &.is-visible {
      display: block;
      border-left: 1px solid $color-borders;
    }
  }

  @include media($min: $util-bar-large) {
    display: flex;
    flex: 0 1 auto;
    height: 100%;
  }

  @include media($max: $screen-large, $min: $util-bar-medium) {
    display: flex;
    flex: 0 1 auto;
    height: 100%;
  }

  .catalog-util-label {
    @include media($min: $util-bar-large) {
      display: inline-flex;
      align-items: center;
    }

    @include media($max: $screen-large, $min: $util-bar-medium) {
      display: inline-flex;
      align-items: center;
    }

    @include media($min: $util-bar-large, $max: $screen-xlarge) {
      display: none;
    }
  }
}

.facet-block {
  &.is-overflowing {
    visibility: hidden;
  }
}

.facet-block-title {
  display: none;
  font-family: $font-secondary;

  .dropdown-more-menu & {
    display: block;
    margin-bottom: $space-smaller;
  }
}


.more-menu {
  .dropdown-toggle {
    color: $color-primary;
    transform: translateX(0);
  }
}

.dropdown-more-menu {
  @include layout-spacing((padding-top, padding-right, padding-bottom, padding-left));
  flex-wrap: wrap;
  border-top: 1px solid $color-borders;
  border-bottom: 1px solid $color-borders;

  &.visible {
    display: flex !important;
  }

  .facet-block-content {
    flex-grow: 1;
    padding-left: 0;
    margin-bottom: $space;
  }
}

.facet-block-content {
  padding-right: $space-small;
  padding-left: $space-small;
}

.facet-item-link {
  display: flex;
  align-items: center;
  color: $color-text-light;
  transition: color 0.2s ease;

  &:hover {
    opacity: 1;
    color: $color-primary;
  }
}

.facet-item-count,
.rating-and-up-text {
  margin-left: $space-smallest;
  font-size: $font-size-body-smaller;
}

// Range
.facet-control-range {
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
  max-width: rem(230);

  .form-field {
    width: calc(50% - #{$space-smaller / 2});
  }

  .form-actions {
    margin-top: $space-smaller;
  }
}

// Hierarchy, Multi
.facet-control-hierarchy,
.facet-control-multi {
  .facet-item {
    &:not(:last-child) {
      margin-bottom: $space-smallest;
    }
  }

  .checkmark {
    @extend %checkmark;
  }

  .checkbox-box {
    @extend %checkbox-unchecked;
    margin-right: $space-smallest;
    text-align: center;
    color: contrast($color-primary, 100%);

    svg {
      width: rem(10);
    }
  }

  .active {
    .checkbox-box {
      @extend %checkbox-checked;
    }

    .checkmark {
      @extend %checkmark-visible;
    }
  }
}

// Show more
.facet-toggle-more {
  margin-top: $space-smaller;
  color: $color-primary;

  &::before {
    margin-right: rem(5);
    content: "+";
  }
}

product-catalog/product-item.scss:
$local-transition: 0.2s ease-out;

.product-grid-item {
  position: relative;
  overflow: hidden;
  text-align: center;

  &:hover {
    .grid-item-overlay {
      opacity: 1;
    }

    .button {
      bottom: 0;
    }

    .product-badge {
      color: $color-white;
      transform: scale(0.66);
    }
  }

  @media (any-hover:none), (any-hover:on-demand) {
    .grid-item-overlay {
      position: absolute;
      padding: 0;
      margin-top: $space-smaller;
      background-color: transparent;
      opacity: 1;

      .button {
        @include button(primary, x-small, false);
        color: $color-white;
        bottom: auto;
        padding-right: $space-smallest;
        padding-left: $space-smallest;
        display: none;

        & + .button {
          @include button(secondary, x-small, false);
        }
      }
    }
  }

  .rating-stars {
    margin-bottom: $space-smaller;
  }

  .price {
    display: block;
    margin-top: $space-smallest;
    color: $color-text-light;
  }

  .product-badge {
    position: absolute;
    top: 0;
    right: 0;
    z-index: z(product-badge);
    transition: all $local-transition;
    transform-origin: top right;
  }

  .flickity-page-dots {
    bottom: 10px;
  }
}

.grid-item-image-container {
  position: relative;
  overflow: hidden;
}

.grid-item-image {
  @include lazy-loaded-img();
  width: 100%;
  height: 100%;
}

.grid-item-overlay {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  z-index: z(product-item-overlay);
  display: flex;
  align-items: center;
  flex-direction: column;
  justify-content: center;
  padding-right: $space-small;
  padding-left: $space-small;
  background-color: rgba($color-black, 0.5);
  opacity: 0;
  transition: opacity $local-transition;

  .button {
    position: relative;
    bottom: $space;
    width: 100%;
    max-width: rem(220);
    min-width: 0;
    font-size: $font-size-body-smaller;
    transition: bottom $local-transition;
    color:$color-white;

    & + .button {
      margin-top: $space-smaller;
      margin-left: 0;
    }
  }
}

.grid-item-overlay-link {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
}

.product-grid-item-name {
  font-family: $font-secondary;
  margin-top: $space-smaller;
  font-size: $font-size-body;
  line-height: $line-height;
  transition: color 0.2s ease-out;
  font-weight: 700;
  letter-spacing: 0.5px;

  &:hover {
    color: $color-primary;
  }
}

.product-grid-item-out-of-stock-message {
  margin-top: $space-smallest;
  margin-bottom: 0;
  font-size: rem(14);
  display:none;
}

.product-badge {
  @extend %text-style-smallcaps;
  height: rem(40);
  padding-right: 1.25em;
  padding-left: 1.25em;
  line-height: rem(40);
  color: contrast($color-black, 100%);
  background-color: $color-primary;

  &.product-badge-sold-out {
    background-color: $color-black;
    color:$color-white;
  }
}

.compare-checkbox {
  @extend %sr-only;

  &:checked {
    & ~ .compare-checkbox-label {
      .compare-checkbox-box {
        @extend %checkbox-checked;
      }

      .checkmark {
        @extend %checkmark-visible;
      }
    }
  }

  &:focus {
    & ~ .compare-checkbox-label {
      .compare-checkbox-box {
        outline: $color-primary auto 3px;
        outline-offset: 2px;
      }
    }
  }
}

.compare-checkbox-label {
  position: relative;
  display: inline-flex;
  margin-top: $space-smaller;
  font-size: $font-size-body-small;
  color: $color-text-lighter;
  cursor: pointer;

  .compare-checkbox-box {
    @extend %checkbox-unchecked;
    margin-right: $space-smallest;
    overflow: hidden;
    color: contrast($color-primary, 100%);
  }

  svg {
    width: rem(10);
  }

  .checkmark {
    @extend %checkmark;
  }
}

// Product item carousels
.flickity-prev-next-button {
  width: rem(50);
  height: rem(50);
  color: contrast($color-background, 40%);
  background-color: contrast($color-background, 10%);
  border-radius: 0;
  transition: background-color $local-transition;

  &.previous {
    @include layout-spacing-half(left);
  }

  &.next {
    @include layout-spacing-half(right);
  }

  &:hover {
    background-color: contrast($color-background, 15%);
  }

  svg {
    transform: scale(0.5);
  }

  .arrow {
    fill: currentColor;
  }
}

product-catalog/quick-view.scss:
