components/footer/footer-section:
{{!
  Additional Navigation lists

  @param section  options
         'pages', 'newsletter', 'social' and 'brands' to determine the order of the lists
         in the footer
}}

<div
  class="site-footer-section site-footer-{{section}}">
  {{#if section '==' 'pages'}}
    {{> components/common/link-lists
      type=section
    }}
  {{/if}}

  {{#if section '==' 'social'}}
    {{> components/common/link-lists
      type=section
    }}
  {{/if}}

  {{#if section '==' 'brands'}}
    {{> components/common/link-lists
      type=section
    }}
  {{/if}}

  {{#if settings.show_newsletter_box}}
    {{#if section '==' 'newsletter'}}
      {{> components/common/newsletter-form}}
    {{/if}}
  {{/if}}

  {{#if section '==' 'address'}}
    {{> components/common/contact-info}}
  {{/if}}
</div>

components/common/link-lists:
{{!
  Link lists

  @param type  options
         'pages', 'social' and 'brands' to determine content of the link list
}}

<div class="link-list-wrapper">
  <ul class="link-list">
    {{#if type '==' 'pages'}}
      <li class="link-list-title">
        {{lang 'link-list.pages'}}
      </li>
      {{#each pages}}
        <li class="link-list-item">
          <a href="{{url}}">
            {{name}}
          </a>
        </li>
      {{/each}}
      {{#if settings.gift_certificates_enabled}}
        <li class="link-list-item">
          <a href="{{urls.gift_certificate.purchase}}">
            {{lang 'link-list.gift_certificates'}}
          </a>
        </li>
      {{/if}}
      {{#if theme_settings.show_sitemap}}
        <li class="link-list-item sitemap">
          <a href="{{urls.sitemap}}">{{lang 'sitemap.title'}}</a>
        </li>
      {{/if}}
    {{/if}}

    {{#if type '==' 'brands'}}
      <li class="link-list-title">
        {{lang 'link-list.brands'}}
      </li>
      {{#each shop_by_brand}}
        <li class="link-list-item">
          <a href="{{url}}">{{name}}</a>
        </li>
      {{/each}}
      <li class="link-list-item">
        <a href="{{urls.brands}}" class="link-list-view-all">
          {{lang 'link-list.view_brands'}}
        </a>
      </li>
    {{/if}}

    {{#if type '==' 'social'}}
      <li class="link-list-title">
        {{lang 'link-list.social'}}
      </li>
      {{#each social_media}}
        <li class="link-list-item">
          <a href="{{url}}" class="{{display_name}}" target="_blank">
            {{name}}
          </a>
        </li>
      {{/each}}
    {{/if}}
  </ul>
</div>
