components/faceted-search/facet-block:
{{!--
  Displays a facet block in the util bar of catalog pages
--}}

{{#if visible}}
  <div
    class="dropdown facet-block {{#unless items.length}}no-items{{/unless}}"
    data-dropdown
    data-facet-index="{{@index}}"
  >
    <button class="dropdown-toggle" data-dropdown-toggle data-tether-disable>
      {{title}}
      {{> svg/icon-dropdown}}
    </button>
    <div class="dropdown-panel catalog-util-dropdown-panel" data-dropdown-panel>
      {{> components/faceted-search/facet-block-content index=@index}}
    </div>
  </div>
{{/if}}

components/reviews/ratings:
{{!--
  Display rating as series of stars

  @param rating
    The Rating value to display.
--}}

<div
  class="rating-stars"
  aria-label="{{lang 'product.reviews.rating_stars' rating=rating}}"
>
  {{#for 1 5}}
    {{#if ../rating '>=' $index}}
      {{> svg/icon-star-full}}
    {{else}}
      {{> svg/icon-star-empty}}
    {{/if}}
  {{/for}}
</div>
