components/search/content-results:
{{#if content_results}}
  {{#each content_results}}
    <article class="content-result">
      <div class="content-result-type">
        {{lang 'search.content_type' type=type}}
      </div>
      <h1 class="content-result-title">
        <a href="{{url}}">{{title}}</a>
      </h1>

      <div class="content-result-summary">
        {{content}}
      </div>

      <div class="content-result-summary-actions">
        <a class="content-result-read-more" href="{{url}}">
          {{ lang 'blog.read_more' }}
        </a>
      </div>
    </article>
  {{/each}}
  {{> components/common/pagination pagination.content_results}}
{{else}}
  <h4>{{lang 'search.no_content_results'}}</h4>
{{/if}}

components/common/pagination:
{{!--
  Pagination: Previous / Next buttons and a list of links to other pages.
  @param this Object containing links, count, etc
--}}

{{#any previous links next}}
  <nav class="pagination">
    {{#if previous}}
      <a
        class="pagination-item pagination-previous"
        href="{{previous}}"
        data-faceted-search-facet
      >
        {{> svg/chevron-left}}
        <span class="sr-only">
          {{lang 'common.pagination.previous'}}
        </span>
      </a>
    {{/if}}

    {{#each links}}
      <a
        class="pagination-item {{#if number '==' ../current}} pagination-current{{/if}}"
        href="{{url}}"
        data-faceted-search-facet
      >
        {{number}}
      </a>
    {{/each}}

    {{#if next}}
      <a
        class="pagination-item pagination-next"
        href="{{next}}"
        data-faceted-search-facet
      >
        {{> svg/chevron-right}}
        <span class="sr-only">
          {{lang 'common.pagination.next'}}
        </span>
      </a>
    {{/if}}
  </nav>
{{/any}}
