components/product-catalog/catalog-index:
{{!--
  Product List -- Index

  Wrapper around the product-list content that takes care of assigning the
  values that the child templates will need. This is the template to call
  via ajax when updating product list content.

  # Summary of Component Tree:

  Base Page: Category / Brand / Search
    Product-Catalog Index (ajax content root)
      Grid
        Product Items

--}}

{{#if category}}

  {{> components/product-catalog/grid
    catalog_type=category
    pagination=pagination.category
  }}
{{/if}}

{{#if brand}}
  {{> components/product-catalog/catalog-util-bar
    catalog_type=brand
    pagination=pagination.brand
  }}

  {{> components/product-catalog/grid
    catalog_type=brand
    pagination=pagination.brand
  }}
{{/if}}

{{#if product_results}}
  {{> components/product-catalog/catalog-util-bar
    catalog_type=product_results
    pagination=pagination.product_results
  }}

  {{> components/search/brand-category}}

  {{> components/product-catalog/grid
    catalog_type=product_results
    pagination=pagination.product_results
  }}
{{/if}}

components/product-catalog/grid:
{{!--
  Product Grid

  @param products
    The list of product items to loop over / display.
--}}

{{#if catalog_type.products.length}}
  <div class="product-grid grid-container">
    {{#each catalog_type.products}}
      {{> components/product-catalog/product-grid-item
        compare=../catalog_type.show_compare
        quickshop=../theme_settings.product_catalog_quickshop
        image_ratio=../theme_settings.product_grid_image_ratio
        fit=../theme_settings.product_grid_image_cropping
        lazyload=../theme_settings.lazyload_mode
        fallback_size=../theme_settings.product-list
        default_image=../theme_settings.default_image
        show_rating=../../settings.show_product_rating
        show_actions=true
        price_range_display=../theme_settings.price_range_display
        retail_price_label=../theme_settings.retail-price-label
        non_sale_price_label=../theme_settings.non-sale-price-label
        sale_price_label=../theme_settings.sale-price-label
        price_label=../theme_settings.price-label
        sale_badges=../theme_settings.sale-badges
      }}
    {{/each}}
  </div>
{{else}}
  <h4>{{lang 'common.no_products'}}</h4>
{{/if}}

{{> components/common/pagination pagination}}

components/search/brand-category:
{{#any brand_results category_results}}
  <div class="search-result-group-wrapper">
    {{#if brand_results.length}}
      <div class="search-result-group search-brand">
        <h1 class="search-results-title">{{lang 'search.brand_title'}}</h1>
        {{#each brand_results}}
          <div class="search-result search-result-brand">
            <a href="{{url}}">{{name}}</a>
          </div>
        {{/each}}
      </div>
    {{/if}}

    {{#if category_results.length}}
      <div class="search-result-group search-category">
        <h1 class="search-results-title">{{lang 'search.category_title'}}</h1>
        <ul>
          {{#each category_results}}
            <li class="search-result search-result-category">
              {{#each this}}
                <a href="{{url}}">{{name}}</a>
                {{#unless @last}} > {{/unless}}
              {{/each}}
            </li>
          {{/each}}
        </ul>
      </div>
    {{/if}}
  </div>
{{/any}}

components/product-catalog/catalog-util-:
{{!--
  Product Catalog Utils - toolbar to refine product lists

  @param catalog_type
    The section this display applies to.

  @param pagination
    The pagination object for this section (for sort / results)
--}}

<div class="catalog-util-bar-wrapper">
  <div class="catalog-util-bar-toggle">
    <button
      class="button button-primary button-x-small"
      data-util-bar-toggle
      aria-label="{{lang 'faceted_search.show_controls'}}"
    >
      {{> svg/icon-filter}}
    </button>
  </div>

  <div class="catalog-util-bar">
    <button class="util-panel-close">
      {{> svg/icon-x}}
    </button>

    {{#if catalog_type.faceted_search_enabled}}

      {{! Facet dropdowns}}
      <div class="facet-block-container more-menu-container" data-util-panel="facet">
        <span class="catalog-util-label">
          {{lang 'faceted_search.filter_by'}}:
        </span>

        {{#each catalog_type.facets}}
          {{#if items}}
            {{> components/faceted-search/facet-block}}
          {{/if}}
          {{#if facet '===' 'price'}}
            {{> components/faceted-search/facet-block}}
          {{/if}}
        {{/each}}
      </div>

      {{! More filters menu}}
      <div
        class="dropdown more-menu"
        data-dropdown data-dropdown-options='{
          "constraints": [
            {
              "to": "body",
              "pin": ["left", "right"]
            }
          ]
        }'
      >
        <button class="dropdown-toggle" data-dropdown-toggle>
          {{lang 'faceted_search.more_filters'}}
        </button>
        <div class="dropdown-panel dropdown-fullwidth dropdown-more-menu" data-dropdown-panel>
          {{#each catalog_type.facets}}
            {{> components/faceted-search/facet-block-content index=@index}}
          {{/each}}
        </div>
      </div>

    {{else}}

      {{#any theme_settings.show_shop_by_price theme_settings.shop_by_brand}}
        <span class="catalog-util-label">
          {{lang 'common.shop_by.label'}}:
        </span>
      {{/any}}

      {{! Shop by Price}}
      {{#if theme_settings.shop_by_price}}
        {{#unless catalog_type '==' product_results}}
          <div class="dropdown" data-dropdown>
            <button class="dropdown-toggle" data-dropdown-toggle>
              {{> components/product-catalog/shop-by-price-label
                shop_by=catalog_type.shop_by_price
              }}
              {{> svg/icon-dropdown}}
            </button>
            <div
              class="dropdown-panel catalog-util-dropdown-panel"
              data-dropdown-panel
            >
              {{> components/product-catalog/shop-by-price
                shop_by=catalog_type.shop_by_price
              }}
            </div>
          </div>
        {{/unless}}
      {{/if}}

      {{! Shop by Brand}}
      {{#if theme_settings.shop_by_brand}}
        <div class="dropdown" data-dropdown>
          <button class="dropdown-toggle" data-dropdown-toggle>
            {{> components/product-catalog/shop-by-brand-label
              shop_by=shop_by_brand
            }}
            {{> svg/icon-dropdown}}
          </button>
          <div
            class="dropdown-panel catalog-util-dropdown-panel"
            data-dropdown-panel
          >
            {{> components/product-catalog/shop-by-brand shop_by=shop_by_brand}}
          </div>
        </div>
      {{/if}}

    {{/if}}

    {{! Sort}}
    <div class="sort-block-container" data-util-panel="sort">
      <div
        class="dropdown dropdown-sort"
        data-dropdown
        data-dropdown-options='{
          "constraints": [
            {
              "to": ".product-catalog",
              "pin": ["left", "right"]
            }
          ]
        }'
      >
        <span class="catalog-util-label">{{lang 'common.sorter.sort_by'}}:</span>
        <button class="dropdown-toggle" data-dropdown-toggle>
          {{> components/product-catalog/sort-label sort=pagination.sort}}
          {{> svg/icon-dropdown}}
        </button>
        <div class="dropdown-panel catalog-util-dropdown-panel" data-dropdown-panel>
          {{> components/product-catalog/sort sort=pagination.sort}}
        </div>
      </div>
    </div>
  </div>

  {{! Active filters}}
  {{#any catalog_type.facets selected=true}}
    <div class="catalog-util-active-filters">
      {{#each catalog_type.facets}}
        {{#each items}}
          {{#if selected}}
            <a class="catalog-util-active-filter" href="{{url}}" data-faceted-search-facet data-facet-remove>
              {{#if ../../facet '===' 'rating'}}
                {{#for 1 param_value}}
                  {{> svg/icon-star-full}}
                {{/for}}
                <span class="rating-and-up-text">
                  {{{lang 'faceted_search.and_up'}}}
                </span>
              {{else}}
                {{title}}
              {{/if}}

              {{> svg/icon-x}}
            </a>
          {{/if}}
        {{/each}}
      {{/each}}

      <a class="clear-all" href="{{catalog_type.url}}" data-faceted-search-facet>
        {{lang 'faceted_search.clear_all'}}
      </a>
    </div>
  {{/any}}

  <div class="catalog-util-button-wrapper">
    <button class="button button-primary" data-util-panel-toggle="facet">{{lang 'common.filter'}}</button>
    <button class="button button-primary" data-util-panel-toggle="sort">{{lang 'common.sort'}}</button>
  </div>
</div>

components/product-catalog/product-grid-:
{{!--
  Product Grid Item - As displayed within a grid listing of products.

  @param product object
    The product object to display, handed over from the loop

  @param compare bool
    Whether to show "compare" checkboxes

  @param quickshop theme-setting
    Theme setting to enable / disable quickshop

  @param image_ratio theme-setting
    The image ratio for this grid

  @param fit theme-setting
    The image size for this grid (cover/contain)

  @param default_image theme-setting
    The default image if none exists

  @param show_rating platform-setting
    Platform setting to toggle ratings

  @param price_range_display theme-setting
    Whether to show the price range or not

  @param retail_price_label theme-setting
    Label for retail price

  @param non_sale_price_label theme-setting
    Label for crossed price on sales

  @param sale_price_label theme-setting
    Label for sale price

  @param price_label theme-setting
    Label for price
--}}

<article class="grid-item product-grid-item">
  {{#unless out_of_stock_message}}
    {{#if sale_badges}}
      {{> core/products/sale-badge
        classes="product-badge product-badge-sale"
      }}
    {{/if}}
  {{else}}
    {{> core/products/oos-badge
      classes="product-badge product-badge-sold-out"
    }}
  {{/unless}}

  <div class="grid-item-image-container {{image_ratio}}" data-quick-shop="{{id}}">
      {{> components/common/responsive-image
        image=image
        class="grid-item-image"
        fit=fit
        fallback_size=fallback_size
        lazyload=lazyload
        default_image=default_image
      }}
    <div class="grid-item-overlay">
      {{#all show_rating num_reviews}}
        {{> components/reviews/ratings
          rating=rating
        }}
      {{/all}}    
      <a class="grid-item-overlay-link" href="{{url}}"></a>
      {{#if show_actions}}
        {{> components/product-catalog/product-grid-item-actions
          quickshop=quickshop
        }}
      {{/if}}
    </div>
  </div>
    <h2 class="product-grid-item-name">
    <a href="{{url}}">{{name}}</a>
  </h2>

  {{> core/products/price
    price_range_display=price_range_display
    retail_price_label=retail_price_label
    non_sale_price_label=non_sale_price_label
    sale_price_label=sale_price_label
    price_label=price_label
  }}

  {{#if out_of_stock_message}}
    <p class="product-grid-item-out-of-stock-message">
      {{out_of_stock_message}}
    </p>
  {{/if}}

  {{#if compare}}
    <div class="grid-item-compare">
      <input
        id="compare-{{id}}"
        class="compare-checkbox"
        type="checkbox"
        data-compare-checkbox
        data-compare-id="{{id}}"
        data-compare-title="{{name}}"
        data-compare-url="{{url}}"
        data-compare-thumbnail="{{getImage image 'product-list' (cdn default)}}"
      >
      <label class="compare-checkbox-label" for="compare-{{id}}">
        <span class="compare-checkbox-box">
          {{> svg/icon-checkmark}}
        </span>

        {{lang 'core.product.compare'}}
      </label>
    </div>
  {{/if}}
</article>

components/common/pagination:
{{!--
  Pagination: Previous / Next buttons and a list of links to other pages.
  @param this Object containing links, count, etc
--}}

{{#any previous links next}}
  <nav class="pagination">
    {{#if previous}}
      <a
        class="pagination-item pagination-previous"
        href="{{previous}}"
        data-faceted-search-facet
      >
        {{> svg/chevron-left}}
        <span class="sr-only">
          {{lang 'common.pagination.previous'}}
        </span>
      </a>
    {{/if}}

    {{#each links}}
      <a
        class="pagination-item {{#if number '==' ../current}} pagination-current{{/if}}"
        href="{{url}}"
        data-faceted-search-facet
      >
        {{number}}
      </a>
    {{/each}}

    {{#if next}}
      <a
        class="pagination-item pagination-next"
        href="{{next}}"
        data-faceted-search-facet
      >
        {{> svg/chevron-right}}
        <span class="sr-only">
          {{lang 'common.pagination.next'}}
        </span>
      </a>
    {{/if}}
  </nav>
{{/any}}

