pages/amp/category:
---
category:
  shop_by_price: true
  products:
    limit: {{theme_settings.product_catalog_limit}}
---

{{> core/amp/pages/category
  header_background=theme_settings.color_background
  header_text=theme_settings.color_text
  body_background=theme_settings.color_background
  color_headings=theme_settings.color_headings
  color_text=theme_settings.color_text
  footer_background=theme_settings.color_highlight
  footer_text=theme_settings.color_text
  color_accent=theme_settings.color_primary
  color_borders=theme_settings.color_borders
}}

{{#partial 'amp-cart-icon'}}
  <div class="amp-mini-cart">
    {{> components/header/mini-cart}}
  </div>
{{/partial}}

{{#partial 'amp-custom-header-styles'}}
  {{> components/amp/styles/mini-cart}}
{{/partial}}

{{#partial 'amp-product-listing'}}
  {{> core/amp/category/product-listing
    default=theme_settings.default_image
    as_low_as=theme_settings.product_grid_as_low_as_pricing
    image_ratio=theme_settings.product_grid_image_ratio
    image_cropping=theme_settings.product_grid_image_cropping
    catalog_columns="two"
    info_alignment="center"
  }}
{{/partial}}

{{#partial 'amp-footer'}}
  {{> core/amp/common/footer
    theme_credits=theme_settings.theme_credits
    theme_name="Brixton"
    store_copyright=theme_settings.store_copyright
  }}
{{/partial}}

{{#partial 'amp-social'}}
  {{> components/common/link-lists
    type='social'
  }}
{{/partial}}

{{#partial 'amp-custom-footer-styles'}}
  {{> components/amp/styles/social-links}}
{{/partial}}
{{> layout/amp}}

core/amp/pages/category:
{{inject "productsPerPage" theme_settings.product_catalog_limit}}

{{#partial "head"}}
  {{#if pagination.category.previous}}
    <link rel="prev" href="{{pagination.category.previous}}">
  {{/if}}
  {{#if pagination.category.next}}
    <link rel="next" href="{{pagination.category.next}}">
  {{/if}}
{{/partial}}

{{#partial "amp-style"}}
  {{> core/amp/category/styles
    header_background=../header_background
    header_text=../header_text
    body_background=../body_background
    color_headings=../color_headings
    color_text=../color_text
    footer_background=../footer_background
    footer_text=../footer_text
    color_accent=../color_accent
    color_borders=../color_borders
  }}
{{/partial}}

{{#partial "page"}}
  {{> core/amp/common/header}}
  <section class="amp-product-catalog">
    {{#if category.image}}
      <div class="amp-product-catalog-image">
        <amp-img
          src="{{getImage category.image 'zoom_size'}}"
          width="300"
          height="150"
          layout="responsive"
          alt="{{category.name}}"
        >
        </amp-img>
      </div>
    {{/if}}

    <div class="amp-product-catalog-header">
      <h1 class="amp-product-catalog-image-title">
        {{category.name}}
      </h1>
      {{{sanitize category.description}}}
      {{{snippet 'categories'}}}
    </div>

    <div class="amp-product-catalog-content">
      {{#if category.products.length}}
        {{#block "amp-product-listing"}} {{/block}}
      {{else}}
        <h4 class="amp-product-catalog-empty">
          {{lang 'core.amp.no_products'}}
        </h4>
      {{/if}}
    </div>
  </section>

  {{> core/amp/category/subcategories}}
  {{#block "amp-footer"}} {{/block}}

  {{#if settings.amp_analytics_id}}
    <amp-analytics type="googleanalytics">
      <script type="application/json">
      {
        "vars": {
          "account": "{{settings.amp_analytics_id}}"
        },
        "extraUrlParams": {
          "cd1": "{{page_type}}",
          "cd2": "{{category.id}}",
          "cd3": "{{category.url}}",
          "cd4": "{{category.name}}"
        },
        "triggers": {
          "trackPageview": {
            "on": "visible",
            "request": "pageview"
          },
          "trackClickOnProductCard" : {
            "on": "click",
            "selector": ".amp-grid-item-link, .amp-grid-item-name a",
            "request": "event",
            "vars": {
              "eventCategory": "Category",
              "eventAction": "Product Card"
            },
            "extraUrlParams": {
              "cd6": "${productLink}",
              "cd7": "${productId}",
              "cd8": "${productName}"
            }
          }
        }
      }
      </script>
    </amp-analytics>
  {{/if}}

{{/partial}}

components/amp/styles/mini-cart:
