  z-index: 10;
}

.amp-mini-cart {
  margin-right: 15px;
}

.amp-header-button {
  position: relative;
  display: flex;
  align-self: center;
  justify-content: center;
  flex-shrink: 0;
  width: 18px;
  height: 12px;
  margin-left: 15px;
  cursor: pointer;
  background-color: transparent;
  border: 0;
}

.amp-header-close {
  position: absolute;
  right: 0;
  padding: 10px;
  margin-left: 0;
}

