{{#if quickshop}}
  <button
    class="button button-primary button-small"
    data-quick-view="view"
    data-product-id="{{id}}"
  >
    {{lang 'product.quick_view'}}
  </button>
{{/if}}
{{#if show_cart_action}}
  {{#if has_options}}
    <button
      class="button button-primary button-small"
      data-quick-view="add"
      data-product-id="{{id}}"
    >
      {{lang 'product.select_options'}}
    </button>
  {{else}}
    {{#if add_to_cart_url}}
      <a
        class="button button-primary button-small button-progress"
        href="{{add_to_cart_url}}"
        data-add-to-cart="{{id}}"
        data-button-purchase
        data-product-title="{{name}}"
      >
        <span class="button-text">
          {{lang 'product.atc'}}
        </span>
        <span class="complete-text">
          {{> svg/icon-checkmark}}
        </span>
      </a>
    {{/if}}
  {{/if}}
{{/if}}

components/navigation/main-navigation:
{{!--
  Main navigation displayed on sidebar

  @param type string
    The type of navigation to show: either categories or pages

  @param navigation object
    The list of links to show: either categories or pages

  @param show_all bool
    Whether to show "all" before parent link
--}}
<nav
  class="main-navigation
  {{#if navigation '==' categories}}
    categories-navigation
  {{else}}
    pages-navigation
  {{/if}}"
  data-main-navigation="{{type}}"
>
  <div class="main-navigation-breadcrumbs" data-navigation-breadcrumbs>
    <button
      class="main-navigation-breadcrumb
      navigation-go-back"
      data-toggle-navigation="{{type}}"
      data-navigation-return
    >
      <span class="navigation-go-back-desktop">
        {{> svg/chevron-left}}
      </span>
      {{lang 'header.go_back'}}
    </button>
    <button
      class="main-navigation-breadcrumb
      reset-all"
      data-navigation-reset
    >
    {{#if navigation '==' categories}}
      {{lang 'header.navigation_all_categories'}}
    {{else}}
      {{lang 'header.navigation_all_pages'}}
    {{/if}}
    </button>
    <span class="main-navigation-panel-title">
      {{#if navigation '==' categories}}
        {{lang 'header.navigation_categories'}}
      {{/if}}
      {{#if navigation '==' pages}}
        {{lang 'header.navigation_pages'}}
      {{/if}}
    </span>
  </div>
  <div class="main-navigation-container">
    <ul
      class="main-navigation-panel
        main-navigation-panel-parent
        is-active"
        data-panel-depth="0"
        data-navigation-menu="{{type}}"
        data-navigation-panel
        data-navigation-panel-parent
      >
      {{#each navigation}}
        <li
          class="main-navigation-item
          main-navigation-item-parent
          {{#if children}}has-children{{/if}}"
          data-navigation-item-parent
        >
          {{> components/navigation/navigation-item-loop
            show_all=../show_all
            show_count=../show_count
          }}
        </li>
      {{/each}}
      {{#if navigation '==' pages}}
        {{#if settings.gift_certificates_enabled}}
          <li
            class="main-navigation-item
            main-navigation-item-parent"
            data-navigation-item-parent
          >
            <a href="{{urls.gift_certificate.purchase}}">
              {{lang 'link-list.gift_certificates'}}
            </a>
          </li>
        {{/if}}
        {{#if theme_settings.show_sitemap}}
          <li
            class="main-navigation-item
            main-navigation-item-parent"
            data-navigation-item-parent
          >
            <a href="{{urls.sitemap}}">
              {{lang 'sitemap.title'}}
            </a>
          </li>
        {{/if}}
      {{/if}}
    </ul>
  </div>
</nav>

components/common/link-lists:
{{!
  Link lists

  @param type  options
         'pages', 'social' and 'brands' to determine content of the link list
}}

<div class="link-list-wrapper">
  <ul class="link-list">
    {{#if type '==' 'pages'}}
      <li class="link-list-title">
        {{lang 'link-list.pages'}}
      </li>
      {{#each pages}}
        <li class="link-list-item">
          <a href="{{url}}">
            {{name}}
          </a>
        </li>
      {{/each}}
      {{#if settings.gift_certificates_enabled}}
        <li class="link-list-item">
          <a href="{{urls.gift_certificate.purchase}}">
            {{lang 'link-list.gift_certificates'}}
          </a>
        </li>
      {{/if}}
      {{#if theme_settings.show_sitemap}}
        <li class="link-list-item sitemap">
          <a href="{{urls.sitemap}}">{{lang 'sitemap.title'}}</a>
        </li>
      {{/if}}
    {{/if}}

    {{#if type '==' 'brands'}}
      <li class="link-list-title">
        {{lang 'link-list.brands'}}
      </li>
      {{#each shop_by_brand}}
        <li class="link-list-item">
          <a href="{{url}}">{{name}}</a>
        </li>
      {{/each}}
      <li class="link-list-item">
        <a href="{{urls.brands}}" class="link-list-view-all">
          {{lang 'link-list.view_brands'}}
        </a>
      </li>
    {{/if}}

    {{#if type '==' 'social'}}
      <li class="link-list-title">
        {{lang 'link-list.social'}}
      </li>
      {{#each social_media}}
        <li class="link-list-item">
          <a href="{{url}}" class="{{display_name}}" target="_blank">
            {{name}}
          </a>
        </li>
      {{/each}}
    {{/if}}
  </ul>
</div>

core/products/price-range:
{{!
  Shows a product's price range.

  @param price_range object

  @param show_amp_classes bool
    If true include amp class component
}}
<div
  class="
    price
    {{#if show_amp_classes}}
      {{> core/amp/common/price-class}}
    {{/if}}
  "
>
  {{#and price_range.min.with_tax price_range.max.with_tax}}
    <div class="price-withtax" data-product-price-wrapper="with-tax">
      <span data-product-price-with-tax class="price price-value">
        {{price_range.min.with_tax.formatted}} - {{price_range.max.with_tax.formatted}}
      </span>
      {{#and price_range.min.without_tax price_range.max.without_tax}}
        <span class="price-tax-label">
          {{lang
            'core.price.with_tax'
            tax_label=price.tax_label
          }}
        </span>
      {{/and}}
    </div>
  {{/and}}
  {{#and price_range.min.without_tax price_range.max.without_tax}}
    <div class="price-withouttax" data-product-price-wrapper="without-tax">
      <span data-product-price-without-tax class="price price-without-tax">
        {{price_range.min.without_tax.formatted}} - {{price_range.max.without_tax.formatted}}
      </span>
      {{#and price_range.min.with_tax price_range.max.with_tax}}
        <span class="price-tax-label">
          {{lang
            'core.price.without_tax'
            tax_label=price.tax_label
          }}
        </span>
      {{/and}}
    </div>
  {{/and}}

  <span class="price-saved" data-product-price-saved></span>
</div>

components/footer/footer-section:
{{!
  Additional Navigation lists

  @param section  options
         'pages', 'newsletter', 'social' and 'brands' to determine the order of the lists
         in the footer
}}

<div
  class="site-footer-section site-footer-{{section}}">
  {{#if section '==' 'pages'}}
    {{> components/common/link-lists
      type=section
    }}
  {{/if}}

  {{#if section '==' 'social'}}
    {{> components/common/link-lists
      type=section
    }}
  {{/if}}

  {{#if section '==' 'brands'}}
    {{> components/common/link-lists
      type=section
    }}
  {{/if}}

  {{#if settings.show_newsletter_box}}
    {{#if section '==' 'newsletter'}}
      {{> components/common/newsletter-form}}
    {{/if}}
  {{/if}}

  {{#if section '==' 'address'}}
    {{> components/common/contact-info}}
  {{/if}}
</div>

components/common/currency-selector:
{{#if currency_selector.currencies.length ">" 1}}
  <div class="currency-selector" data-currency-selector>
    <span class="currency-selected" data-currency-selector-toggle>
      {{currency_selector.active_currency_code}}
    </span>
    <svg xmlns="http://www.w3.org/2000/svg" width="18" height="18" viewBox="0 0 18 18" role="presentation">
      <path id="a" d="M12 8l-3 3-3-3h6z" fill="currentColor" />
    </svg>
    <select class="currencies" aria-label="{{lang 'header.currency_selector'}}">
      {{#each currency_selector.currencies}}
        <option value="{{{switch_url}}}" {{#if is_active}}selected{{/if}}>
          {{name}}
        </option>
      {{/each}}
    </select>
  </div>
{{/if}}

components/common/share-widget:
{{!--
  Share Links - Displayed on Blog Posts and Product Pages

  @param url
    The URL of the post that should be shared to social media.

  @param share_buttons
    The data for the buttons to be shared from Bigcommerce

  @param share_type (optional)
    Added as a class name to the share-links-container

  @param image
    The image to add to the pinterest post
--}}

{{#if settings.add_this.buttons.length}}
  <div class="dropdown product-share-block" data-dropdown>
    <button class="dropdow-toggle share-dropdown-toggle" data-dropdown-toggle>
      <span class="share-icon">
        {{> svg/icon-share}}
      </span>
      {{ lang 'product.share_item'}}
    </button>
    <div class="dropdown-panel share-dropdown-panel" data-dropdown-panel>
      {{> components/common/share-links
        url=product.url
        title=product.title
        share_type='product-post'
        share_buttons=settings.add_this
        image=(getImage product.main_image 'share')
      }}
    </div>
  </div>
{{/if}}

components/common/wishlist:
{{#if customer.wishlists.length}}
  <div
    class="dropdown"
    data-dropdown
    data-dropdown-options='{
      "constraints": [
        {
          "to": ".single-product-page",
          "pin": ["left", "right"]
        }
      ]
    }'
  >
    <a class="dropdown-toggle button button-secondary button-progress" href="#" data-dropdown-toggle>
      <span class="button-text">
        {{lang 'product.add_to_wishlist'}}
      </span>
      <span class="complete-text">
        {{> svg/icon-checkmark}}
      </span>
    </a>
    <ul class="dropdown-panel product-dropdown-panel" data-dropdown-panel>
     {{#each customer.wishlists}}
       <li><a href="{{../product.add_to_wishlist_url}}&wishlistid={{id}}" data-wishlist="{{view_url}}">{{name}} ({{num_items}})</a></li>
     {{/each}}
    </ul>
  </div>
{{else}}
  <a class="button button-secondary" href="{{product.add_to_wishlist_url}}">{{lang 'product.add_to_wishlist'}}</a>
{{/if}}

core/forms/common/multiline:
{{!
  Textarea form component

  @param validation_message string
         Custom validation message
}}
<div class="form-field form-field-common form-field-multiline {{#if required}}form-required{{/if}}" id="{{id}}">
  <label class="form-label">
    <span class="form-field-title">
      {{label}}
      {{#if required}}<span class="required-text">{{lang 'common.required'}}</span>{{/if}}
    </span>
    <span class="form-field-control">
      <textarea
        class="form-input"
        id="textarea-{{id}}"
        name="{{name}}"
        placeholder="{{prefill}}"
        rows="{{rows}}"
        {{#if required}}
          required
          {{#if validation_message}}
            data-vd-message-required="{{validation_message}}"
          {{/if}}
        {{/if}}
        aria-required="{{required}}">{{value}}</textarea>
    </span>
  </label>
</div>
