components/cart/item:
{{!--
  Cart Item: an individual product item shown on the shopping cart page.
--}}

<article class="cart-item" data-cart-item data-item-id="{{id}}">
  <div class="cart-item-image cart-item-section">
    {{#if type '===' 'GiftCertificate'}}
      {{> svg/giftcard}}
    {{else}}
      <a href={{url}}>
        {{> components/common/responsive-image
          image=image
          fit=fit
          fallback_size=fallback_size
          lazyload=lazyload
          default_image=default_image
        }}
      </a>
    {{/if}}
  </div>

  <div class="cart-item-details cart-item-section">
    <h1 class="cart-item-title">
      <a href="{{url}}">{{{name}}}</a>
    </h1>
    {{> components/cart/item-options}}
    {{> components/cart/gift-wrap-item}}
  </div>

  <div class="cart-item-unit-price cart-item-section">
    {{#any bulk_pricing.discount_percentage bulk_pricing.discount_amount}}
      <div class="cart-item-discount-line">
        <span class="cart-item-price-label">
          {{lang 'cart.base_price'}}
        </span>
        <span class="price-is-discounted">
          {{bulk_pricing.base_price.formatted}}
        </span>
      </div>

      <div class="cart-item-discount-line">
        <span class="cart-item-price-label">
          {{lang 'cart.bulk_discount'}}
        </span>
        {{price.formatted}}
      </div>
    {{else}}
      <div>{{price.formatted}}</div>
    {{/any}}
  </div>

  <div class="cart-item-quantity cart-item-section">
    {{> components/common/quantity}}
  </div>

  <div class="cart-item-total-price cart-item-section">
    {{#if total_discounted}}
      <div class="price-is-discounted">{{total.formatted}}</div>
      <div>{{total_discounted.formatted}}</div>
    {{else}}
      <div class="cart-item-price">{{total.formatted}}</div>
    {{/if}}
  </div>

  <a class="cart-item-remove" href="{{remove_url}}" data-cart-item-remove>
    {{> svg/icon-x}}
  </a>
</article>

components/cart/item-options:
{{#or options configurable_fields}}
  <div class="cart-item-options">
    {{#each configurable_fields}}
      <div class="cart-item-option-item">
        <span class="cart-item-option-label">{{name}}:</span>
        <span class="cart-item-option-value">
          {{#if is_file}}
            <a href="/viewfile.php?prodfield={{../id}}&cartitem={{../../id}}">{{{value}}}</a>
          {{else}}
            {{{value}}}
          {{/if}}
        </span>
      </div>
    {{/each}}

    {{#each options}}
      <div class="cart-item-option-item">
        <span class="cart-item-option-label">{{name}}:</span>
        <span class="cart-item-option-value">
          {{#if is_file}}
            <a href="/viewfile.php?prodfield={{../id}}&cartitem={{../../id}}">{{{value}}}</a>
          {{else}}
            {{{value}}}
          {{/if}}
        </span>
      </div>
    {{/each}}
  </div>

  <button class="cart-item-edit-button" data-item-edit="{{id}}">
    {{lang 'cart.change_options'}}
  </button>
{{/or}}

components/cart/gift-wrap-item:
{{!--
  Gift Wrap Item - Shown as part of cart-items
  with links to add / change / remove gift wrapping for that item
--}}

{{inject 'removeGiftWrap' (lang 'cart.gift_wrapping.remove_confirm')}}

{{#if show_gift_wrapping}}
  <div class="cart-item-giftwrapping">
    {{#if gift_wrapping}}

      {{! Show type of giftwrap and message }}
      <div class="cart-item-giftwrap-info">
        <span class="cart-item-giftwrap-type">{{lang 'cart.gift_wrapping.gift_wrap' wrap=(limit gift_wrapping.name 30)}}</span>
        {{#if gift_wrapping.message}}
          <span class="cart-item-giftwrap-message">{{lang 'cart.gift_wrapping.message' type=gift_wrapping.message}}</span>
        {{/if}}
      </div>

      {{! Change / Remove }}
      <div class="cart-item-giftwrap-control">
        <a class="primary-link" href="#" data-item-giftwrap="{{id}}">{{lang 'cart.gift_wrapping.change'}}</a>
        /
        <a class="primary-link" href="{{gift_wrapping.remove_url}}" data-giftwrap-remove>{{lang 'cart.gift_wrapping.remove'}}</a>
      </div>

    {{else}}

      {{! Add Giftwrapping }}
      <button class="cart-item-giftwrap-control" data-item-giftwrap="{{id}}">
        {{lang 'cart.gift_wrapping.add'}}
      </button>

    {{/if}}
  </div>
{{/if}}
